            {% for i in range(1, n_cols + 1) -%}
                a.col_{{ i }} = b.col_{{ i }} {% if not loop.last %}and{% endif %}
            {% endfor -%}
        {% else %}
            a cross join b
        {% endif %}
    )
    -- DEBUG:
    -- select * from final
    select
        *
    from final
    where
        {% if tolerance_percent %}
